import re
import time
from urllib.parse import urlparse
import nltk
from nltk.tokenize import word_tokenize
nltk.download('stopwords')
nltk.download('punkt')
from bs4 import BeautifulSoup
from selenium import webdriver
from selenium.webdriver.chrome.service import Service

service = Service(executable_path='C:\\Users\\јбдул\\PycharmProjects\\selenium_practice\\chromedriver\\yandexdriver.exe')

driver = webdriver.Chrome(service=service)
url = "http://www.sberbank.ru/ru/person"
sber_urls = []
another_urls= []
netlocs = ['www.sberbank.ru','www.sberbank.com', 'sberbank.com', 'sberbank.ru']
try:
    driver.get(url)
    time.sleep(5)
    soup = BeautifulSoup(driver.page_source, "html.parser")
    for link in soup.findAll('a'):
        if link.get('href')[:1] == 'h':
            if urlparse(link.get('href')).netloc in netlocs:
                sber_urls.append(link.get('href'))
            else:
                another_urls.append(link.get('href'))
    CLEANR = re.compile('<.*?>')
    words = re.sub(CLEANR, '', soup.text)
    res = word_tokenize(words)
    print(res)
except Exception as e:
    print(e)
finally:
    driver.close()
    driver.quit()

